import axios from 'axios';
export default {
  name: 'App',
  data() {
    return {
      userData: null
    };
  },
  mounted() {
    var tokenValue = this.$store.state.authData;
    if (tokenValue && tokenValue != null) {
      axios.defaults.headers.common = {
        authorization: tokenValue
      };
    } else {
      axios.defaults.headers.common = {};
    }
  }
};
