import axios from 'axios';

import config from '@/config';

const REGISTER_USER_PATH = (type) => type ? `register/${type}` : "register";
const LOGIN_USER_PATH = () => "login";
const GET_USER_PATH = (id) => `/users/${id}`


export default {

    loginUser : function(loginData,callback){
        axios.post( 
            config.BASE_URL + LOGIN_USER_PATH() , 
            loginData
        ).then((response)=>{
            callback(response.data);
        }).catch((error)=>{

        });
    },
    getUser: function(id, callback) {
        axios.get (
            config.BASE_URL + GET_USER_PATH(id),
        ).then((response)=>{
            console.log(response);
        })
    }
};
